import getFormattedDate from "../../../utils/getFormattedDate";
import Card from "react-bootstrap/Card";
import {Button, Col, Row} from "react-bootstrap";

const Comment = ({ auth, comment, onDelete, onEdit}) => {
  const postDate = getFormattedDate(comment.date);
  var user = (auth.isAuthenticated) ? JSON.stringify(auth.user.user_name).slice(1,-1):""
  return (
      <div class="row">
        <div class="col-md-12">
          <strong>{comment.author}</strong>
          <span class="pull-right">{postDate}</span>
          <p>
            {/*{comment.text}*/}
            {comment.body}
          </p>
          {(auth.isAuthenticated && user === comment.author) ? (
                  <Row className="mt-4">
                    {/*<Col className="text-center">*/}
                      <Button
                          className="mr-2"
                          variant="outline-info"
                          onClick={onEdit}
                      >
                        Edit
                      </Button>
                      <Button className="mr-2" variant="outline-danger"  onClick={onDelete}>
                        Delete
                      </Button>
                      {/*TODO: onclick = onComment*/}
                      <Button variant="outline-success" onClick={onDelete}>
                        Comment
                      </Button>
                    {/*</Col>*/}
                  </Row>
              ):
              ""
          }
      </div>
</div>

  );
};

// Post.propTypes = {
//   post: PropTypes.object.isRequired
// };

export default Comment;
