import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import PostForm from "../../components/posts/PostForm";
import Validate from "../../components/form/Validate";
import { connect } from "react-redux";
import { createPost } from "../../actions/postActions";
import CommentForm from "../../components/posts/comment/CommentForm";

const CreateCommentPage = ({ errors, createPost, loading, history }) => {
  const [post, setPost] = useState({
    // title: "",
    body: "",
    errors: {}
  });

  useEffect(() => {
    setPost(post => {
      return { ...post, errors };
    });
  }, [errors]);

  const handleChange = e => {
    setPost({
      ...post,
      [e.target.name]: e.target.value
    });
  };

  const handleBlur = e => {
    const { name, value } = e.target;
    const error = { ...post.errors, ...Validate(name, value).errors };
    setPost({ ...post, errors: { ...error } });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const { title, body } = post;
    createPost({ title, body }, history);
  };

  return (
      <CommentForm
          loading={loading}
          post={post}
          onChange={handleChange}
          onBlur={handleBlur}
          onSubmit={handleSubmit}
      />
  );
};

const mapStateToProps = state => ({
  loading: state.post.postLoading,
  errors: state.errors
});

CreateCommentPage.propTypes = {
  createPost: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
  loading: PropTypes.bool.isRequired
};

export default connect(
    mapStateToProps,
    { createPost }
)(CreateCommentPage);
