import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import PostForm from "../../components/posts/PostForm";
import Validate from "../../components/form/Validate";
import { connect } from "react-redux";
// import { createPost } from "../../actions/postActions";
import CommentForm from "../../components/posts/comment/CommentForm";
import { createComment } from "../../actions/postActions";

const CreateCommentPage = ({ post, errors, createComment, loading, history }) => {
  const [comment, setComment] = useState({
    // title: "",
    body: "",
    errors: {}
  });

  useEffect(() => {
    setComment(comment => {
      return { ...comment, errors };
    });
  }, [errors]);

  const handleChange = e => {
    setComment({
      ...comment,
      [e.target.name]: e.target.value
    });
  };

  const handleBlur = e => {
    const { name, value } = e.target;
    const error = { ...comment.errors, ...Validate(name, value).errors };
    setComment({ ...comment, errors: { ...error } });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const { body } = comment;
    createComment({ body }, history, post._id);

  };

  return (
      <CommentForm
          loading={loading}
          post={comment}
          onChange={handleChange}
          onBlur={handleBlur}
          onSubmit={handleSubmit}
      />
  );
};

const mapStateToProps = state => ({
  loading: state.post.postLoading,
  errors: state.errors,
  post: state.post.post
});

// const mapStateToProps = state => ({
//   auth: state.auth,
//   post: state.post.post
// });

CreateCommentPage.propTypes = {
  createComment: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
  loading: PropTypes.bool.isRequired
};

export default connect(
    mapStateToProps,
    { createComment }
)(CreateCommentPage);
