  const { body, errors } = comment;
  return (
      <Container>
        <Row>
          <Col className="mx-auto">
            <Form noValidate onSubmit={onSubmit} className="p-sm-3 p-xs-1">
              {/*<Input*/}
              {/*    name="title"*/}
              {/*    type="text"*/}
              {/*    placeholder="Enter Post Title"*/}
              {/*    value={title}*/}
              {/*    onChange={onChange}*/}
              {/*    onBlur={onBlur}*/}
